import { createSlice, current } from "@reduxjs/toolkit";
import users from "../../data/Users.json";
import chats from "../../data/Chats.json";

const modifyChatsList = (chatsList, chatInfo, isDelete) => {
  const chatId = chatInfo.id;
  const filteredChatList = chatsList.filter((chat) => chat.id !== chatId);
  if (!isDelete) {
    filteredChatList.unshift(chatInfo);
  }
  return filteredChatList;
};

const chatsSlice = createSlice({
  name: "chats",
  initialState: {
    chatsList: chats,
    chatInfo: null,
    usersList: users,
    matchedUsers: [],
    isAddUserModalOpen: false,
    userSession: {
      id: 7,
      name: "Sayantan Roy",
      img: "data:image/jpeg;base64,",
    },
  },
  reducers: {
    getChats: (state, action) => {
      const sortedChats = [...state.chatsList].sort(
        (c1, c2) => c2.latestMessageTimeStamp - c1.latestMessageTimeStamp
      );
      state.chatsList = sortedChats;
    },
    showChatInfo: (state, action) => {
      let chatObj = action.payload.chatObj,
        tempObj;
      if (action.payload.source === "SEARCH") {
        tempObj = state.chatsList.find(
          (chat) => chat.entityId === chatObj.entityId
        );
        chatObj = tempObj ? tempObj : chatObj;
      }
      state.chatInfo = chatObj;
    },
    sendMessage: (state, action) => {
      const chatInfo = JSON.parse(JSON.stringify(state.chatInfo));
      let newChatsList = [];
      const timeNow = new Date().getTime();
      if (!chatInfo.hasOwnProperty("id")) {
        chatInfo.id = timeNow;
      }
      chatInfo.latestMessageTimeStamp = timeNow;
      chatInfo.messages.push({
        id: timeNow,
        content: action.payload.message,
        sentBy: "Sayantan Roy",
        userId: state.userSession.id,
        timeStamp: timeNow,
      });
      newChatsList = modifyChatsList([...state.chatsList], chatInfo, false);
      state.chatsList = newChatsList;
      state.chatInfo = chatInfo;
    },
    searchEntity: (state, action) => {
      const searchStr = action.payload.query;
      let matchedUsers = [];
      if (searchStr.length) {
        matchedUsers = state.usersList.filter((user) => {
          return user.name.toLowerCase().includes(searchStr.toLowerCase());
        });
      }
      state.matchedUsers = matchedUsers;
    },
    clearSearchSuggestions: (state, action) => {
      state.matchedUsers = [];
    },
    removeChat: (state, action) => {
      const chatInfo = JSON.parse(JSON.stringify(state.chatInfo));
      const newChatsList = modifyChatsList(
        [...state.chatsList],
        chatInfo,
        true
      );
      state.chatsList = newChatsList;
      state.chatInfo = null;
    },
    toggleAddNewUserModal: (state, action) => {
      state.isAddUserModalOpen = !state.isAddUserModalOpen;
    },
    addNewUser: (state, action) => {
      const usersList = [...state.usersList];
      const lastUserId = usersList[usersList.length - 1].id;
      usersList.push({
        id: lastUserId + 1,
        name: action.payload.name,
        img: "data:image/jpeg;base64,",
      });
      state.usersList = usersList;
      state.isAddUserModalOpen = false;
    },
  },
});

export const {
  getChats,
  showChatInfo,
  sendMessage,
  searchEntity,
  clearSearchSuggestions,
  removeChat,
  toggleAddNewUserModal,
  addNewUser,
} = chatsSlice.actions;
export default chatsSlice.reducer;
